import React, { Component } from "react";

import UserService from "../services/user.service";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: ""
    };
  }

  componentDidMount() {

    ProducesService.getCurrentProduces().then((res) => {
      this.setState({produceds: res.data});
    },
    error => {
      this.setState({
        content:
          (error.response && error.response.data) ||
          error.message ||
          error.toString()
      });
    }
    );

    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
        });
      },

    );
  }

  render() {
    return (
      <div className="list-item">
        {
          this.state.produceds.map(
            food =>
            <div className="item">
              <div>
                <img src={food.linkimage} alt="food"/>
                <h5 className="list-Title-food">{food.namefood}</h5>
                <div className="list-price">Giá:{food.price}</div>
                <button className="list-btn">Đặt món</button>
              </div>
            </div>
          )
        }


      </div>
    );
  }
}
