import React, { Component } from "react";
import ProducesService from "../services/produce.service";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style/StyleProduced.css'

export default class produced extends Component {
  constructor(props) {
    super(props);

    this.state = {
      produceds: [
        namefood = '',
        price = '',
        detail = '',
        idCategory = '',
        linkimage = ''
      ]

    };
  }

  componentDidMount() {
    this.setState(this.state.produceds = ProducesService.getCurrentProduces());
  }

  render() {

    return (
      <div className="list-item">
        {
          this.state.produceds.map(
            <div className="item">
              <div>
                <img src={this.state.produceds.linkimage} />
                <h5 className="list-Title-food">{this.state.produceds.namefood}</h5>
                <div className="list-price">Giá:{this.state.produceds.price}</div>
                <button className="list-btn">Đặt món</button>
              </div>
            </div>
          )
        }


      </div>
    );
  }
}
