import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import AuthService from "../services/auth.service";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style/StyleProduced.css'

import {
  Card, CardImg, CardText,
  h5, Button
} from 'reactstrap';

export default class produced extends Component {
  constructor(props) {
    super(props);

    this.state = {
      namefood : '',
      price : '',
      detail : '',
      idCategory:''
    };
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/produced" });
    this.setState({ currentUser: currentUser, userReady: true })
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }

    const { currentUser } = this.state;

    return (
        <div className="list-item">
          <div className="item">
            <div>
              <h5>Cơm chiên</h5>
              <CardText>Nguyễn Bình</CardText>
              <Button>Đặt món</Button>
            </div>
          </div>

          <div className="item">
            <div>
              <h5 tag="h5">Cơm chiên</h5>
              <CardText>Nguyễn Bình</CardText>
              <Button>Đặt món</Button>
            </div>
          </div>

          <div className="item">
            <div>
              <h5 tag="h5">Cơm chiên</h5>
              <CardText>Nguyễn Bình</CardText>
              <Button>Đặt món</Button>
            </div>
          </div>

          <div className="item">
            <div>
              <h5 tag="h5">Cơm chiên</h5>
              <CardText>Nguyễn Bình</CardText>
              <Button>Đặt món</Button>
            </div>
          </div>
        </div>
    );
  }
}
