import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import AuthService from "../services/auth.service";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

export default class produced extends Component {
  constructor(props) {
    super(props);

    this.state = {
      namefood : '',
      price : '',
      detail : '',
      idCategory:''
    };
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/produced" });
    this.setState({ currentUser: currentUser, userReady: true })
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }

    const { currentUser } = this.state;

    return (
        <div>
          <Card>
            <CardImg top width="100%" src="../logo.svg" alt="Card image cap" />
            <CardBody>
              <CardTitle tag="h5">{this.state.namefood}ádád</CardTitle>
              <CardSubtitle tag="h6" className="mb-2 text-muted">Card subtitle</CardSubtitle>
              <CardText>{this.state.detail}sdsdsfs</CardText>
              <Button>Đặt món</Button>
            </CardBody>
          </Card>
        </div>
    );
  }
}
