const Utils = {
  env: {
    isIOS () {
      if(process.env.RUNTIME==='native'){
        const { platform } = weex.config.env;
        return platform.toLowerCase() === 'ios';
      }else{
        return false;
      }
      
    },
    isAndroid () {
      if(process.env.RUNTIME==='native'){
        const { platform } = weex.config.env;
        return platform.toLowerCase() === 'android';
      }else{
        return false;
      }
      
    }  
  }

};

export default Utils;
